import React, { useState } from "react";
import ToDoItems from "../components/ToDoItems"

function App() {
  const [note, handleNote] = useState("");

  function handleChange(value) {
    console.log(value);
    handleNote(value);
  }

  const [addNote, handleAddNote] = useState([]);

  function handleClick() {
    handleAddNote((prevNotes) => {
      return [...prevNotes, note];
    });
    handleNote("");
  }

  function deleteItem(id){
    handleAddNote(prevNote =>{
      return prevNote.filter((currentNote,index) =>{
        return index !== id;
      });
    });

    }

  const handleKeyPress = (key) =>{
    key === "Enter" && handleClick();
  }


  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input
          value={note}
          onChange={(e) => handleChange(e.target.value)}
          onKeyPress = {(k) =>{handleKeyPress(k.key)}
          }
        type="text"
        />
        <button onClick={handleClick}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {addNote.map((everyNote, index) => (
            <ToDoItems 
            key = {index}
            id ={index}
            text ={everyNote}
            onChecked ={deleteItem} />
          ))}
        </ul>
          {/* {addNote.map((everyNote) =>
          {
              return(
                <ToDoItems text={everyNote}/>
              );
          })} */}
      </div>
    </div>
  );
          }

export default App;
